def lengthOfLastWord(s: str) -> int:
    if len(s) == 1:
        return 1
    rev = s[::-1]
    length = 0
    while rev[0] == " ":
        rev = rev[1:]

    while len(rev) != 0 and rev[0] != " ":
        length += 1
        rev = rev[1:]
    return length

