def minimumOperations(nums: list[int]) -> int:
    operations = 0

    if len(nums) == 0:
        return 0

    while len(nums) >= 3:
        if len(set(nums)) == len(nums):
            return operations

        nums = nums[3:]

        operations +=1

    if len(set(nums)) == len(nums):
        return operations

    operations += 1

    return operations

