def isMonotonic(nums: list[int]) -> bool:
    diffs = []
    for i in range(len(nums)-1):
        if i+1 > (len(nums)-1):
            break
        diff = nums[i] - nums[i+1]
        if diff != 0:
            diffs.append(int(diff/abs(diff)))
        else:
            diffs.append(diff)
        print(diffs)
    if -1 in diffs and 1 in diffs:
        return False
    return True

nums = [1,3,2]
print(isMonotonic(nums))

