def repeatedSubstringPattern(s: str) -> bool:
    if len(s) == 0 or len(s) == 1:
        return False
    if len(set(list(s))) == 1:
        return True
    for i in range(1,len(s)):
        temp = s
        sub = s[:i]
        if len(temp)%len(sub) == 0:
            sizeFactor = len(temp)/len(sub)
            if temp == int(sizeFactor)*sub:
                return True
    return False

