def reverse(s: str) -> str:
    # ASCII CODES
    # A-Z: 65-90
    # a-z: 97-122

    letters = ""
    positions = []

    i = 0
    for char in s:

        asc = ord(char)

        if asc in range(65, 91) or asc in range(97, 123):
            letters += char
        else:
            positions.append(i)

        i += 1


    if len(letters) == 0:
        return s

    reverse = letters[::-1]

    new = ""

    for pos in range(0,len(s)):
        if len(positions) != 0 and pos == positions[0]:
            new += str(s[positions[0]])
            positions.pop(0)
        else:
            new += reverse[0]
            if len(reverse) != 1:
                reverse = reverse[1:]

    return new

